// Split long text into TTS-friendly chunks
const input = $input.first().json;
const fullScript = input.output || input.text || '';

// Function to split text into chunks under 4000 characters
function splitIntoChunks(text, maxLength = 4000) {
  const sentences = text.split(/(?<=[.!?])\s+/); // Split on sentence endings
  const chunks = [];
  let currentChunk = '';

  for (const sentence of sentences) {
    // If adding this sentence would exceed limit, start new chunk
    if (currentChunk.length + sentence.length > maxLength) {
      if (currentChunk) {
        chunks.push(currentChunk.trim());
        currentChunk = sentence;
      } else {
        // Single sentence too long, force split
        chunks.push(sentence.substring(0, maxLength));
        currentChunk = sentence.substring(maxLength);
      }
    } else {
      currentChunk += (currentChunk ? ' ' : '') + sentence;
    }
  }

  // Add final chunk
  if (currentChunk) {
    chunks.push(currentChunk.trim());
  }

  return chunks;
}

// Split the script
const textChunks = splitIntoChunks(fullScript);

// Return array of chunks for processing
return textChunks.map((chunk, index) => ({
  json: {
    input_text: chunk,
    voice: "echo",
    model: "gpt-4o-mini-tts", 
    response_format: "mp3",
    chunk_number: index + 1,
    total_chunks: textChunks.length
  }
}));
